sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    g_form.clearOptions('u_what_needed');
    var requestType = newValue;
    var whatNeededChoices = [];

    switch (requestType) {
    case 'hr':
      whatNeededChoices = ['Human Resources 1', 'Human Resources 2', 'Other'];
      break;
    case 'facilities':
      whatNeededChoices = ['Facilities 1', 'Facilities 2', 'Other'];
      break;
    case 'legal':
      whatNeededChoices = ['Legal 1', 'Legal 2', 'Other'];
      break;
    default:
      break;
    }

    g_form.addOption('what_needed', '', '');
    for (var i = 0; i < whatNeededChoices.length; i++) {
        g_form.addOption('u_what_needed', whatNeededChoices[i], whatNeededChoices[i]);
    }

}
